mapper.select:
SELECT tr.id, tr.task_name, tr.leader, tr.status taskRecord_status, tr.install_begin_time, tr.install_end_time, m.status machine_status, m.nameplate, mo.id machine_order_id, mo.status machineOrder_status, mo.machine_type mo_machine_type, mo.order_num, mo.*, task.standard_minutes FROM task_record tr join process_record pr on tr.process_record_id = pr.id join machine m on m.id = pr.machine_id join machine_order mo on mo.id = m.order_id join task on task.task_name = tr.task_name where 1=1
and tr.id = #{taskRecordId}
and task.task_name = #{taskName}
and mo.order_num like CONCAT('%','${machineOrderNumber}','%' )
and DATE_FORMAT(tr.install_begin_time,'%Y-%m-%d') >= #{queryStartTime}
and DATE_FORMAT(tr.install_end_time,'%Y-%m-%d') <= #{queryFinishTime}
and m.nameplate like CONCAT('%','${nameplate}','%' )

select
*
from task_record
WHERE 1=1

    and id=#{id}



    and process_record_id=#{processRecordId}

delete.if:
DELETE FROM task_record WHERE process_record_id=#{processRecordId}

    AND node_key=#{nodeKey}


    AND `status`=#{status}

mapper.select:
select
tr.id, tr.task_name,tr.leader, tr.worker_list, tr.status taskRecord_status, tr.install_begin_time,
tr.install_end_time,tr.quality_begin_time, tr.quality_end_time,
m.machine_strid,m.nameplate,m.machine_type,m.is_urgent,
mo.order_num,mo.contract_ship_date, mo.plan_ship_date,mo.all_urgent,
tp.plan_time, tp.plan_type, tp.deadline,tp.id task_plan_id
from task_record tr
join process_record pr on tr.process_record_id = pr.id
join machine m on m.id = pr.machine_id
join machine_order mo on mo.id = m.order_id
JOIN task_plan tp on tr.id = tp.task_record_id
join task on task.task_name = tr.task_name
WHERE 1 = 1

    and m.machine_strid = #{machine_strid}


    and mo.order_num = #{order_num}


    and m.nameplate = #{nameplate}



        and tr.status = 6


        and tr.status != 6



    and tr.task_name = #{task_name}


    and m.machine_type = #{machine_type}



    and DATE_FORMAT(tp.plan_time,'%Y-%m-%d') >= #{query_start_time}


    and DATE_FORMAT(tp.plan_time,'%Y-%m-%d') <= #{query_finish_time}

ORDER By tp.plan_time DESC,tp.update_time DESC

select tr.id, tr.task_name, tr.process_record_id, tr.node_key, tr.leader, tr.worker_list, tr.status taskRecord_status, tr.install_begin_time, tr.install_end_time,tr.quality_begin_time, tr.quality_end_time, pr.id process_record_id_in_association, pr.*, m.id machine_id1,m.status machine_status, m.*, mo.id machine_order_id, mo.status machineOrder_status, mo.machine_type mo_machine_type, mo.*, tp.id task_plan_id,tp.*
from task_record tr join task_plan tp on tp.task_record_id = tr.id join process_record pr on tr.process_record_id = pr.id join machine m on m.id = pr.machine_id join machine_order mo on mo.id = m.order_id
where m.nameplate = #{namePlate}

select tr.id, tr.task_name, tr.process_record_id, tr.node_key, tr.leader, tr.worker_list, tr.status taskRecord_status, tr.install_begin_time, tr.install_end_time,tr.quality_begin_time, tr.quality_end_time, pr.id process_record_id_in_association, pr.*, m.id machine_id1,m.status machine_status, m.*, mo.id machine_order_id, mo.status machineOrder_status, mo.machine_type mo_machine_type, mo.*, oll.id order_loading_list_id,oll.*, tp.id task_plan_id,tp.*
from task_record tr join task_plan tp on tp.task_record_id = tr.id join process_record pr on tr.process_record_id = pr.id join machine m on m.id = pr.machine_id join machine_order mo on mo.id = m.order_id join order_loading_list oll on oll.order_id = mo.id
join task on task.task_name = tr.task_name join install_group on install_group.id = task.group_id join user on user.group_id = install_group.id where m.nameplate = #{namePlate}

SELECT tr.*, m.nameplate, mo.order_num FROM task_record tr RIGHT JOIN process_record pr ON tr.process_record_id = pr.id RIGHT JOIN machine m ON m.id = pr.machine_id RIGHT JOIN machine_order mo ON mo.id = m.order_id WHERE tr.task_name = #{task_name} AND DATE_FORMAT(install_begin_time, '%Y-%m-%d') >= #{install_start_time}
AND DATE_FORMAT(install_begin_time, '%Y-%m-%d') <= #{install_finish_time}
ORDER BY order_num DESC, install_begin_time;
